mapper.select:
SELECT 
        com_start 
FROM tbl_commute
WHERE com_d8 = #{com_d8}
AND PRJ_IN_NO = (SELECT 
                        PRJ_IN_NO 
                FROM TBL_PROJECT_IN tpi 
                WHERE EMAIL = #{email}
                AND PRJ_NO = ${prj_no}
                )

SELECT 
     com_end 
FROM tbl_commute
WHERE com_d8 = #{com_d8}
AND PRJ_IN_NO = (SELECT 
                        PRJ_IN_NO 
                FROM TBL_PROJECT_IN tpi 
                WHERE EMAIL = #{email}
                AND PRJ_NO = ${prj_no}
                )

SELECT 
     NVL(max(com_start),'')com_start,NVL(max(com_end),'')com_end  
FROM tbl_commute  
WHERE com_d8 = #{com_d8}
AND PRJ_IN_NO = (SELECT 
                        PRJ_IN_NO 
                FROM TBL_PROJECT_IN tpi 
                WHERE EMAIL = #{email}
                AND PRJ_NO = #{prj_no}
                )
